package basicSamples;

public class OperatorSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		int b =4;
		System.out.println("a+b="+(a+b));
		System.out.println("a-b="+(a-b));
		System.out.println("a*b=" +(a*b));
		System.out.println("a/b=" +(a/b));
		System.out.println("a%b=" +(a%b));
		int ac=8;
		int v = ac;
		System.out.println(v);
		v+=ac;
		System.out.println(v);
		v*=ac;
		System.out.println(v);
		

}
}
